import React, { useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';

import Titular from './Titular.js';
import Frutas from './Frutas.js';
import Formulario from './Formulario.js';
import { obtenerAsuntos } from "./services"; //Toma por defecto el index.js


function App() {
  const [asuntos, setAsuntos] = useState([]);

  useEffect(() => { // Lo ejecuta cuando carga la aplicacion la primera vez
    (async () => {
      const data = await obtenerAsuntos(); //Accedo a los datos que vienen del json
      setAsuntos(data);
      })()
  }, []);

  return (
    <div className="App">
      { 

      (asuntos.length === 0) ? (<p>No hay asuntos para mostrar</p>) : ( 
        <select>

            {asuntos.map(item => (
              <option value={item.value}>
                {item.name}
              </option>
            ))}

        </select>

        )

      }
      <header className="App-header">
          <Formulario />
      </header>
    </div>
  );
}

export default App;
